import registry from 'registry';

export default class Json {

  #session = null;

  constructor(session){
    this.#session = session;
  }

  async prompt(){
    const avatar = this.#session.user.avatar;
    const username = this.#session.username;
    const response = {
      type: 'input',
      name: 'text',
      message: username,
    };
    return response;
  }

  async print(data){
    const username = this.#session.username;
    const avatar = this.#session.user.avatar;
    const response = Object.assign({username},data);
    return response;
  }

  async input({message}){
    const response = {
      message: message,
      initial: ''
    };
    return response;
  }

  async select({message, choices}){
    const response = {
      name: 'value',
      message: message,
      choices,
    };
    return response;
  }

}
