import registry from 'registry';

import Map from 'Map';

export default class Universe {

  #map = new Map();

  constructor(){
  }

  async initialize(){
    await this.#map.initialize();
    registry.universe = this;
  }

  // Return a location, byName
  location(name){
    const response = this.#map.location(name);
    return response;
  }

  // Display Universe
  show(){
    this.#map.show();
  }

}
