import registry from 'registry';

import Avatar from 'Avatar';
import Screen from 'Screen';

export default class User {

  #session = null;
  avatar = null;
  screen = null;

  constructor(session){
    this.#session = session;

    this.avatar = new Avatar(session);
    this.screen = new Screen(session);

  }

  async initialize(){

    await this.avatar.initialize();
    await this.screen.initialize();

  }

}
